import React from 'react';

function ImageComponent({ userText, onUserTextChange, image }) {
  const positions = [
    { top: '3%', left: '5%' },
    { top: '1%', left: '37%' },
    { top: '1%', left: '55%' },
    { top: '1%', left: '70%' },
    { top: '4%', left: '37%' },
    { top: '4%', left: '55%' },
    { top: '4%', left: '70%' },
    { top: '14%', left: '65%' },
  ];

  return (
    <div className="image-container">
      <img src={image} alt="Character Sheet" />
      {positions.map((position, index) => (
        <div className="text-box" style={position} key={index}>
          <input
            type="text"
            placeholder={`Enter text ${index + 1}`}
            value={userText[index]}
            onChange={(e) => {
              const updatedUserText = [...userText];
              updatedUserText[index] = e.target.value;
              onUserTextChange(updatedUserText);
            }}
          />
        </div>
      ))}
    </div>
  );
}

export default ImageComponent;
